    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double a, b, c, d, e, f, g, h, i, j, k, l;
        String Adw;
        String[] grade = new String[12];
        double unit[] = new double[12];
        double[] Grade = new double[12];
        double sum = 0;
        double add = 0;
        double ss = 0;
        //int= "aa";//"bb", "cc", "dd", "ee", "ff", "gg", "hh","ii" , "jj", "kk", "ll";
        try {
            {
            }
            a = Double.parseDouble(Unit1.getText());
            b = Double.parseDouble(Unit2.getText());
            c = Double.parseDouble(Unit3.getText());
            d = Double.parseDouble(Unit4.getText());
            e = Double.parseDouble(Unit5.getText());
            f = Double.parseDouble(Unit6.getText());
            g = Double.parseDouble(Unit7.getText());
            h = Double.parseDouble(Unit8.getText());
            i = Double.parseDouble(Unit9.getText());
            j = Double.parseDouble(Unit10.getText());
            k = Double.parseDouble(Unit11.getText());
            l = Double.parseDouble(Unit12.getText());
            unit[0] = (int) a;
            unit[1] = (int) b;
            unit[2] = (int) c;
            unit[3] = (int) d;
            unit[4] = (int) e;
            unit[5] = (int) f;
            unit[6] = (int) g;
            unit[7] = (int) h;
            unit[8] = (int) i;
            unit[9] = (int) j;
            unit[10] = (int) k;
            unit[11] = (int) l;
            for (int x = 0; x < 12; x++) {
                sum = sum + unit[x];
            }
            UnitSow.setText(Double.
                    toString(sum));

            grade[0] = Grade1.getText();
            grade[1] = Grade2.getText();
            grade[2] = Grade3.getText();
            grade[3] = Grade4.getText();
            grade[4] = Grade5.getText();
            grade[5] = Grade6.getText();
            grade[6] = Grade7.getText();
            grade[7] = Grade8.getText();
            grade[8] = Grade9.getText();
            grade[9] = Grade10.getText();
            grade[10] = Grade11.getText();
            grade[11] = Grade12.getText();
            double[] GradeValue = new double[12];
            for (int x = 0; x < 12; x++) {
                if (grade[x].equalsIgnoreCase("A")) {
                    GradeValue[x] = 4.0;
                } else if (grade[x].equalsIgnoreCase("AB")) {
                    GradeValue[x] = 3.75;

                } else if (grade[x].equalsIgnoreCase("B")) {
                    GradeValue[x] = 3.50;
                } else if (grade[x].equalsIgnoreCase("BC")) {
                    GradeValue[x] = 3.25;

                } else if (grade[x].equalsIgnoreCase("C")) {
                    GradeValue[x] = 3.00;
                } else if (grade[x].equalsIgnoreCase("CD")) {
                    GradeValue[x] = 2.75;

                } else if (grade[x].equalsIgnoreCase("D")) {
                    GradeValue[x] = 2.50;
                } else if (grade[x].equalsIgnoreCase("DE")) {
                    GradeValue[x] = 2.25;
                } else if (grade[x].equalsIgnoreCase("E")) {
                    GradeValue[x] = 2.00;
                } else if (grade[x].equalsIgnoreCase("EF")) {
                    GradeValue[x] = 1.75;
                } else if (grade[x].equalsIgnoreCase("FF")) {
                    GradeValue[x] = 1.50;
                } else if (grade[x].equalsIgnoreCase("F")) {
                    GradeValue[x] = 1.25;
                } else if (grade[x].equalsIgnoreCase("PF")) {
                    GradeValue[x] = 1.00;
                } else if (grade[x].equalsIgnoreCase("Hf")) {
                    GradeValue[x] = 0.0;
                } else {
                    JOptionPane.showMessageDialog(rootPane, "INVALID GRADE" + (x + 1));
                }
            }

             for (int x = 0; x < 12; x++) {
                add = GradeValue[x] + add;
             }
            for (int x = 0; x < 12; x++) {
                Grade[x] = GradeValue[x] * unit[x];
            }
            for (int x = 0; x < 12; x++) {
                ss = ss + Grade[x];
            } double Gpe = ss / sum;
            JOptionPane.showMessageDialog(rootPane, ss);
            JOptionPane.showMessageDialog(rootPane,sum);
            JOptionPane.showMessageDialog(rootPane, Gpe);
           //%.3f Double.toString(Gpe)

            Adw = String.format("%.2f", Gpe);
            GPAshow.setText(Adw);

        } catch (Exception u) {
        }      // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

